package string;

public class incrementPlus1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="india";
		String rev="";
		for (int i = 0; i < s.length(); i++) {
			rev=rev+s.charAt(i);
			System.out.println(rev);
		}
		//System.out.println(rev+" ");
	}

}
